declare module '*?arraybuffer' {
	const src: ArrayBuffer
	export default src
}

declare module '*?uint8array' {
	const src: Uint8Array
	export default src
}

declare module '*?arraybuffer&base64' {
	const src: ArrayBuffer
	export default src
}

declare module '*?uint8array&base64' {
	const src: Uint8Array
	export default src
}
